    //质量变换
    imageMogrStr += '/quality/'.concat(quality);
  }

  if (strip) {
    //去除元信息
    imageMogrStr += '/strip';
  }

  var ext = getFileExt(url);

  // gif 图片不做格式转换，否则会损坏动图
  if (ext === 'gif') {
    imageMogrStr += '/cgif/1';
