    //格式转换
    imageMogrStr += '/format/'.concat(format);
  }

  if (format === 'jpg' || (!format && (ext === 'jpg' || ext === 'jpeg'))) {
    //渐进式 jpg 加载
    imageMogrStr += '/interlace/1';
  }
  if (!imageMogrStr) return url;
  return ''
    .concat(url)
    .concat(url.indexOf('?') !== -1 ? '&' : '?', 'imageMogr2')
    .concat(imageMogrStr);
