@keyframes t-bar {
  0% {
    width: 0;
  }
  50% {
    width: 70%;
  }
  100% {
    width: 80%;
  }
}
@keyframes t-bar-loaded {
  0% {
    height: 6rpx;
    opacity: 1;
    width: 90%;
  }
  50% {
    height: 6rpx;
    opacity: 1;
    width: 100%;
  }
  100% {
    height: 0;
    opacity: 0;
    width: 100%;
  }
}
.t-loading__dot:nth-of-type(1) {
  transform: rotate(30deg);
  opacity: 0;
}
.t-loading__dot:nth-of-type(2) {
  transform: rotate(60deg);
  opacity: 0.08333333;
}
.t-loading__dot:nth-of-type(3) {
  transform: rotate(90deg);
  opacity: 0.16666667;
}
.t-loading__dot:nth-of-type(4) {
  transform: rotate(120deg);
  opacity: 0.25;
}
.t-loading__dot:nth-of-type(5) {
  transform: rotate(150deg);
  opacity: 0.33333333;
}
.t-loading__dot:nth-of-type(6) {
  transform: rotate(180deg);
  opacity: 0.41666667;
}
.t-loading__dot:nth-of-type(7) {
  transform: rotate(210deg);
  opacity: 0.5;
}
.t-loading__dot:nth-of-type(8) {
  transform: rotate(240deg);
  opacity: 0.58333333;
}
.t-loading__dot:nth-of-type(9) {
  transform: rotate(270deg);
  opacity: 0.66666667;
}
.t-loading__dot:nth-of-type(10) {
  transform: rotate(300deg);
  opacity: 0.75;
}
.t-loading__dot:nth-of-type(11) {
  transform: rotate(330deg);
  opacity: 0.83333333;
}
.t-loading__dot:nth-of-type(12) {
  transform: rotate(360deg);
  opacity: 0.91666667;
}
@keyframes rotate {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}
@keyframes rotateReverse {
  from {
    transform: rotate(360deg);
  }
  to {
    transform: rotate(0deg);
  }
}
@keyframes dotting {
  0% {
    opacity: 0.15;
  }
  1% {
    opacity: 0.8;
  }
  33% {
    opacity: 0.8;
  }
  34% {
    opacity: 0.15;
  }
  100% {
    opacity: 0.15;
  }
}

