  display: flex;
  margin: 0 0 0 20rpx;
  flex-direction: row;
  flex: 1 1 auto;
  min-height: 192rpx;
}

.wr-goods-card__long_content {
  display: flex;
  flex-direction: column;
  overflow: hidden;
  flex: 1 1 auto;
}
